/// <reference types="cypress" />

declare namespace Cypress {
  interface Chainable {
    /**
     * Generate and save schema from API response.
     */
    apiSchemaGenerate(
      url: string,
      schemaName: string,
      requestOptions?: Partial<RequestOptions>
    ): Chainable<any>;

    /**
     * Validate API response against schema.
     */
    apiSchemaValidate(
      url: string,
      schemaName: string,
      requestOptions?: Partial<RequestOptions>
    ): Chainable<any>;

    /**
     * Generate schema if missing, else validate. Pass { update: true } to overwrite.
     */
    apiSchemaCheck(
      url: string,
      schemaName: string,
      options?: { update?: boolean; requestOptions?: Partial<RequestOptions> }
    ): Chainable<any>;
  }
}
